<?phpclass LoginBusiness extends Business{    private $model;    private $view;        public function __construct($view,$model)    {        $this->model = $model;        $this->view = $view;    }        public function login()    {            $res = array();        $res["URL"] = ConfigLoad::getConfig()->URL;                if ($this->model->authentication($_POST["user"], $_POST["password"])) {            if ($this->saveSession($_POST["user"])){;                                View::cleanModules();                $this->view->generateDefault();                $res["html"] =$this->view::$modules;                $res["logged_who"] = $_POST["user"];                $res["error"] = 0;                                return json_encode($res);            }            $res["error"] = "Problema na gravacao de sessão";            return json_encode($res);        }        $res["error"] = "Usuário ou senha inválidos.";        return json_encode($res);            }        public function logout()    {                $res = array();        $res["error"] = 0;                if (!$this->clearSession()){            $res["error"] = "Erro ao apagar sessão";        }                view::cleanModules();        $this->view->generateDefault();        $res["html"] =$this->view::$modules;        $res["URL"] = ConfigLoad::getConfig()->URL;                return json_encode($res);            }        private function saveSession($user)    {        $_SESSION["logged_in"]=true;        $_SESSION["logged_who"]=$user;        return true;    }        private function clearSession()    {                unset($_SESSION["logged_in"]);        unset($_SESSION["logged_who"]);        return true;    }}?>